	  ". content ."
	  ". . .";
  }

#projects .content {
	grid-area: content;
	width: 100%;
	padding:1rem;} 

	.portfolio__labels {
		display: flex;
		align-items: center;
		justify-content: space-between;
		flex-wrap: wrap;
		margin-top: 10vh;
		margin-bottom: 5vh;
		padding:1rem;
		width: 50rem;

		& a {
			text-transform: uppercase;
			font-weight: bold;
			color: #333030;
			transition: 0.4s;

			&:hover {color:$secondary;}		  }
	  }



	  .portfolio__container {
		max-width: 100%;
		display: flex;
		align-items: center;
		flex-wrap: wrap;

	  }



/*--------------------------------------------------------------
# Contact Section
--------------------------------------------------------------*/
#contact {
	background-image: linear-gradient(to left, rgba(252, 252, 252, 0.7), rgb(216, 216, 216)), url("/img/projectsbg.png");
	background-size: cover;
	background-position: center;
    width: 100%;
	height: 100vh;
  }

#contact .content {
	text-align:center;
	width: 100%;
	padding:10rem 4rem 4rem 4rem;

	& h4 {color:white;
		font-size:60px;
		font-family: $font-title;
		font-weight:400;}

} 

.field {
	text-align:left;
	.labelname {color:white;
	font-family: $font-text;}
}	

.control input[type=text],input[type=email],textarea {
		background-color: white!important;
		border-radius:10px!important;
		border: 1px solid white!important;
        font-size:12px!important;
		padding:1.3rem!important;
		color:$secondary!important;
	  }
.control input:focus, textarea:focus {
    outline: none!important;
    border:none;
    box-shadow: 0 0 10px $secondary!important;
}	 

form {
	margin:0 auto;
width:60%;
	button {padding:0.7rem 1.5rem 0.7rem 1.5rem;
	border-radius:10px;
	border:none;
	color: $secondary;
    background: white;
	transition:0.5s;
	cursor:pointer;
   & p {font-weight:bold;}}

   button:hover {
	box-shadow: 0 0 10px $secondary;
	color:white;
    background: $secondary;
}   

}


/* --- 5.0 Footer --- */

footer {background-color:$secondary;
	padding:2rem!important;}


